package day023;

public class Ex01_If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//홀짝 판별 예제
		
		int num = 3;
		
		if (num % 2 == 0); {
			System.out.println(num + "는 짝수");
		}
		if( num % 2 != 0) {
			System.out.println(num + "는 홀수");
		}
		
		
		
		
		
	}

}
